import React, { createContext, useState, useEffect } from "react";
import { usePouch } from "use-pouchdb";

const CuponContext = createContext();

const CuponProvider = ({ children }) => {
  const [categoria, setCategoria] = useState();
  const [totalCategorias, setTotalCategorias] = useState([]);
  const [provincia, setProvincia] = useState();
  const [totalProvincias, setTotalProvincias] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [sentData, setSentData] = useState(false);

  const db = usePouch();

  useEffect(() => {
    db?.get("categorias")
      .then((result) => {
        if (result !== undefined) {
          setCategoria(result);
          setIsLoading(false);
        } else {
          setCategoria([]);
          setIsLoading(false);
        }
      })
      .catch((error) => {
        setIsLoading(false);
        console.log(error);
      });
  }, [db, sentData]);

  return (
    <CuponContext.Provider
      value={{
        categoria,
        setCategoria,
        provincia,
        setProvincia,
        totalCategorias,
        setTotalCategorias,
        totalProvincias,
        setTotalProvincias,
        isLoading,
        setIsLoading,
      }}
    >
      {children}
    </CuponContext.Provider>
  );
};

export default CuponContext;
export { CuponProvider };
