import React from "react";
import { useFind } from "use-pouchdb";
import { nanoid } from "nanoid";

import { categorias } from "../data/categorias";

export const UseNewRandomId = (prefix) => {
  return prefix + nanoid();
};

const AddCategoria = (db, data, entity) => {
  var newId = useNewRandomId(`${entity}_`);

  db?.put({
    ...data,
    _id: newId,
    entity: entity,
  });
};

export const firstAddInDatabase = (
  validacion,
  db,
  data,
  entity,
  setSentData
) => {
  if (validacion) {
    try {
      db?.put({ ...data, _id: entity, entity: entity }).then(() => {
        categorias?.map((e) => {
          return AddCategoria(db, e, "categoria");
        });
      });
    } finally {
      setSentData((prevSentData) => !prevSentData);
    }
  }
};
