/// <reference types="pouchdb-core" />
import { ResultType } from './state-machine';
import { CommonOptions } from './utils';
/**
 * Get all docs or a slice of all docs and subscribe to their updates.
 * @param options PouchDB's allDocs options.
 */
export default function useAllDocs<Content>(options?: CommonOptions & (PouchDB.Core.AllDocsWithKeyOptions | PouchDB.Core.AllDocsWithKeysOptions | PouchDB.Core.AllDocsWithinRangeOptions | PouchDB.Core.AllDocsOptions)): ResultType<PouchDB.Core.AllDocsResponse<Content>>;
